INSERT INTO `cat_list` VALUES (1,'Кровати','Кровати от 2 790 руб. от производителя — в наличии ≈ 334 модели с доставкой по Москве, Московской области и России недорого и точно в срок! ⌛','https://pm.ru/global_images/goods/274/958/c4c/927/1109651_goods_label.jpg'),(2,'Диваны','Производитель мебели Bled.ru предлагает диваны и кресла в интернет-магазине мебели. Гарантия на продукцию от 18 месяцев. Быстрая доставка по Москве, МО и России. Самовывоз. 3 собственные фабрики. 6 шоу-румов.','https://www.divan.ru/assets/thumb/product/366208/200x150_ffffff_100_0_2018060409430010800_0.jpg'),(3,'Шкафы','Шкафы - купить шкаф недорого в Москве от производителя, цена в интернет-магазине','https://www.divan.ru/assets/thumb/product/736894/200x150_ffffff_100_0_2019012115405810800_0.jpg'),(4,'Столы','Продажа столов в Москве по выгодным ценам. Купить стол в интернет магазине Bled','https://www.mebelaero.ru/upload/iblock/565/565e4eda44b4e1ccd8e412c1771a3e9a.jpg'),(5,'Стулья','Продажа стульев в Питере по выгодным ценам. Купить стол в интернет магазине Bled','https://www.mebelaero.ru/upload/iblock/710/710b4c980bcd715af58d1e8ab0b914be.jpg');
